sys_ws_operation.operation_script:
(function process( /*RESTAPIRequest*/ request, /*RESTAPIResponse*/ response) {

    //①Perfect Finderテーブル上で条件に合致したレコードを抽出しシリアル番号をWorkspace oneの登録処理対象とする
    //対象テーブル：
    //x_ritsc_a200002101_perfect_finder
    // 抽出条件：
    //機材明細：稼働ステータス名が 「解約済み」「廃棄済み」「紛失」「返却済み」ではない
    //かつ
    // SKYSEA連携フラグが「TRUE」のもの
    //かつ
    //インポート日が当日のもの
    try {
		gs.info('【Client_Security Develop:WorkspaceONE連携:登録対象取得】Started');
        QueryString = 'status!=150^status!=110^status!=120^status!=130^u_skysea_cooperation_flg=true^sys_updated_onONToday@javascript:gs.beginningOfToday()@javascript:gs.endOfToday()';

        var gr = new GlideRecord('x_58872_needit_x_ritsc_a200002101_perfect_finder');
        gr.addEncodedQuery(QueryString);
        gr.orderBy('serial_number');
        gr.query();

        //MIDサーバに渡すシリアル番号のリストをJson形式に変換する
        //同一シリアル番号の場合は連携しない
        var serialList = [];
        var preSerialNum = '';
        while (gr.next()) {
            var serialNum = gr.getValue('serial_number');

            // 前のレコード同一シリアル番号の場合は連携しない
            if (preSerialNum == serialNum) {
                continue;
            }
            preSerialNum = serialNum;
            serialList.push(serialNum);
        }
        response.setStatus(200);
        response.setBody(serialList);
		gs.info('【Client_Security Develop:WorkspaceONE連携:登録対象取得】Ended');
	} catch (e) {
        //例外が発生した場合は500を返す
        response.setStatus(500);
		gs.error("【Client_Security Develop:WorkspaceONE連携:登録対象取得】Abnormal Ended.");
    }
})(request, response);
